    public int findPairs(int[] nums, int k) {

        /**
         1. Count number in array. Map {3 -1, 1 - 2, 4 - 1, 5 - 1}
         2. Iterate through map.
         a. If (K >= map.key) continue;
         b. findNumber = map.getKey() - k;
         Example: 3 - 2 = 1;
         map.containsKey(1) = yes, increament counter and delete key from map.

         We should use ConcurrentHashMap that helps to exclude Exception (ConcurrentModificationException)
         **/

        int answer = 0;

        Map<Integer, Integer> map = new HashMap<>(); //Храним число и сколько оно раз повторяется
        Map<Integer, List<Integer>> mapWithIndex = new HashMap<>(); //Храним число и его индексы

        for (int num : nums) {
            map.merge(num, 1, Integer::sum);
        }

        for (int i = 0; i < nums.length; i++) {
            List<Integer> list;
            if (!mapWithIndex.containsKey(nums[i])) {
                list = new ArrayList<>();
            } else {
                list = mapWithIndex.get(nums[i]);
            }
            list.add(i);
            mapWithIndex.put(nums[i], list);
        }

        /**
         1 - {0,2}
         3 - {1}
         5 - {3}
         4 - {4}
         **/


        for (Map.Entry<Integer, Integer> entry : map.entrySet()) {

            int key = entry.getKey(); //Получил число
            int number = key - k; //Вычисляем число, которое нужно найти в Map

            if (mapWithIndex.containsKey(number)) { //Проверяем, содержится ли наше число в map c индексами.

                //Случай когда k = 0 и вот примерный массив (1, 3, 1, 5, 4) -> {1-2, 3-1, 5-1, 4-1}
                if (number == key) { //Если число, которое ищем равно ключу по которому итерируемся, это говорит о том что у нас k = 0
                    List<Integer> indexes = mapWithIndex.get(number); //Получаем все индексы числа, которого хотим найти
                    if (indexes.size() == 1) { //Если кол-во у нас одно, то мы переходим на другой элемент. Ничего с ним не делаем.
                        continue; //Мы не можем удалить просто число (само себя). У нас должна быть пара. А если бы удаляли так, то это была бы не пара!
                    }
                }

                //А вот если k != 0 то увеличивает ответ и удаляем только из другой Map (mapWithIndex)
                answer++;
                mapWithIndex.remove(number);
            }
        }
        return answer;
    }


    /*TC1:[1,2,3,4,5] - Map{1-1,2-1,3-1,4-1,5-1}, k = 1
        2 - 1 = 1; -> remove it from map {2-1,3-1,4-1}, answer = 1
        3 - 1 = 2; -> remove it from map {3-1,4-1}, , answer = 2
        4 - 1 = 3; -> remove it from map {4-1}, , answer = 3
        5 - 1 = 4; -> remove it from map {4-1}, , answer = 4
    */


    /*TC1:[1,3,1,5,4] - Map{1-2,3-1,5-1,4-1}, k = 0
        1 - 0 = 1; -> remove it from map {3-1,5-1,4-1}, answer = 1
    */


    public int findPairsLT(int[] nums, int k) {

        int result = 0;
        HashMap<Integer, Integer> counter = new HashMap<>();
        for (int n : nums) {
            counter.put(n, counter.getOrDefault(n, 0) + 1);
        }

        for (Map.Entry<Integer, Integer> entry : counter.entrySet()) {
            int x = entry.getKey();
            int val = entry.getValue();
            if (k > 0 && counter.containsKey(x + k)) { //хитро
                result++;
            } else if (k == 0 && val > 1) {
                result++;
            }
        }
        return result;
    }
